settings.cardCss:
.card{
    font-weight: bold;
    font-size: 32px;
    color: black;
    width: 100%;
    height: 100%;
    background-color: black;
}
 .logo-image{
    width: 93px;
    height: 104px;
    border-radius: 20%;
    overflow: hidden;
}
ul {

  list-style-type: none;
  margin: 0;
  padding: 0;
  overflow: hidden;
  background-color: black;
}

li {
  float: left;
}

li a {
  display: block;
    font-weight: bold;
    font-size: 32px;
  color: white;
  text-align: center;
  padding: 14px 16px;
  text-decoration: none;
}

li a:hover:not(.active) {
  background-color: #111;
}

.active {
  background-color: #04AA6D;
}
